mapper.select:
select user_nick
from rny_member
where user_id = #{id}

select user_name
from rny_member
where user_id = #{id}

select user_no
from rny_member
where user_id = #{id}

select *
from rny_member
where user_id = #{id}
and user_pw = #{pw}

SELECT COUNT(*)
FROM rny_member
WHERE user_id = #{id}
